mapper.update:
UPDATE tb_adviser 
SET gmt_modify = now()

    , name = #{name}


    , phone = #{phone}


    , email = #{email}


    , state = #{state}


    , image_url = #{imageUrl}


    , region_id = #{regionId}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM tb_adviser
WHERE 1 = 1

    AND name = #{name}


    AND region_id = #{regionId}

;

SELECT id, name, phone, email, state, image_url AS imageUrl, region_id AS regionId FROM tb_adviser WHERE 1 = 1
AND name = #{name}
AND region_id = #{regionId}
ORDER BY name LIMIT #{offset}, #{rows};
