mapper.select:
SELECT count(*)
FROM b_applicant
WHERE user_id = #{userId}
;

mapper.update:
UPDATE b_applicant
SET gmt_modify = now()

    , surname = #{surname, jdbcType=VARCHAR}


    , firstname = #{firstname, jdbcType=VARCHAR}


    , birthday = #{birthday}


    , type = #{type}


    , visa_code = #{visaCode}


    , visa_expiration_date = #{visaExpirationDate}


    , nut_cloud = #{nutCloud}


    , file_url = #{fileUrl}


    , first_controller_contents = #{firstControllerContents}


    , user_id = #{userId}

WHERE id = #{id};
