mapper.update:
UPDATE b_brokerage 
SET gmt_modify = now()

    , handling_date = #{handlingDate}


    , user_id = #{userId}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , service_id = #{serviceId}


    , receivable = #{receivable}


    , received = #{received}


    , amount = #{amount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , remarks = #{remarks}


    , is_close = #{isClose}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_brokerage
WHERE 1 = 1

    AND name LIKE '%${keyword}%'


     AND handling_date > #{startHandlingDate} AND handling_date < #{endHandlingDate} 


     AND gmt_create > #{startDate} AND gmt_create < #{endDate} 


    AND adviser_id = #{adviserId}


    AND user_id = #{userId}

;

SELECT bb.id AS id, bb.gmt_create AS gmtCreate, bb.handling_date AS handlingDate, bb.user_id AS userId, bb.receive_type_id AS receiveTypeId, bb.receive_date AS receiveDate, bb.service_id AS serviceId, bb.receivable AS receivable, bb.received AS received, bb.amount AS amount, bb.gst AS gst, bb.deduct_gst AS deductGst, bb.bonus AS bonus, bb.adviser_id AS adviserId, bb.official_id AS officialId, bb.remarks AS remarks, bb.is_close AS isClose, tbu.name AS userName, tbu.birthday AS birthday, tbu.phone AS phone FROM b_brokerage bb, tb_user tbu WHERE bb.user_id = tbu.id
AND bb.name LIKE '%${keyword}%'
AND bb.handling_date > #{startHandlingDate} AND bb.handling_date < #{endHandlingDate}
AND bb.gmt_create > #{startDate} AND bb.gmt_create < #{endDate}
AND bb.adviser_id = #{adviserId}
AND bb.user_id = #{userId}
ORDER BY bb.gmt_create DESC LIMIT #{offset}, #{rows};

		select IFNULL(sum(bonus), 0.00) from b_brokerage where DATE_FORMAT(gmt_create, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m') and is_close = 0

            AND bb.adviser_id = #{adviserId}

		;
