mapper.update:
UPDATE b_brokerage_sa 
SET gmt_modify = now()

    , handling_date = #{handlingDate}


    , user_id = #{userId}


    , receive_type_id = #{receiveTypeId}


    , school_id = #{schoolId}


    , start_date = #{startDate}


    , end_date = #{endDate}


    , tuition_fee = #{tuitionFee}


    , commission = #{commission}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , is_close = #{isClose}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_brokerage_sa bbs, b_school bs, tb_user tbu
WHERE bbs.school_id = bs.id AND bbs.user_id = tbu.id

    AND tbu.name LIKE '%${keyword}%'


     AND bbs.handling_date > #{startHandlingDate} AND bbs.handling_date < #{endHandlingDate} 


     AND bbs.start_date > #{startDate} AND bbs.start_date < #{endDate} 


    AND bbs.adviser_id = #{adviserId}


    AND bbs.school_id = #{schoolId}

;

SELECT bbs.id AS id, bbs.gmt_create AS gmtCreate, bbs.handling_date AS handlingDate, bbs.user_id AS userId, bbs.receive_type_id AS receiveTypeId, bbs.school_id AS schoolId, bbs.start_date AS startDate, bbs.end_date AS endDate, bbs.tuition_fee AS tuitionFee, bbs.commission AS commission, bbs.gst AS gst, bbs.deduct_gst AS deductGst, bbs.bonus AS bonus, bbs.adviser_id AS adviserId, bbs.official_id AS officialId, bbs.is_close AS isClose, bs.name AS schoolName, bs.subject AS schoolSubject, tbu.name AS userName, tbu.birthday AS birthday, tbu.phone AS phone FROM b_brokerage_sa bbs, b_school bs, tb_user tbu WHERE bbs.school_id = bs.id AND bbs.user_id = tbu.id
AND tbu.name LIKE '%${keyword}%'
AND bbs.handling_date > #{startHandlingDate} AND bbs.handling_date < #{endHandlingDate}
AND bbs.start_date > #{startDate} AND bbs.start_date < #{endDate}
AND bbs.adviser_id = #{adviserId}
AND bbs.school_id = #{schoolId}
ORDER BY bbs.gmt_create DESC LIMIT #{offset}, #{rows};
