mapper.update:
UPDATE b_commission_order 
SET gmt_modify = now()

    , service_order_id = #{serviceOrderId}


    , installment = #{installment}


    , start_date = #{startDate}


    , end_date = #{endDate}


    , tuition_fee = #{tuitionFee}


    , per_term_tuition_fee = #{perTermTuitionFee}


    , remarks = #{remarks}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_commission_order co, b_service_order so, tb_user u
WHERE co.service_order_id = so.id AND so.user_id = u.id

    AND so.mara_id = #{maraId}


    AND so.adviser_id = #{adviserId}


    AND so.official_id = #{officialId}


    AND u.name LIKE '%${name}%'


    AND u.phone = #{phone}


    AND u.wechat_username = #{wechatUsername}


    AND so.school_id = #{schoolId}


    AND so.is_settle = #{isSettle}


    AND so.state = #{state}

;

SELECT
    co.id,
    co.gmt_create AS gmtCreate,
    co.service_order_id AS serviceOrderId,
    co.installment,
    co.start_date AS startDate,
    co.end_date AS endDate,
    co.tuition_fee AS tuitionFee,
    co.per_term_tuition_fee AS perTermTuitionFee,
    co.remarks,
    co.is_close AS isClose,
    so.amount,
    so.expect_amount AS expectAmount,
    so.user_id AS userId,
    so.school_id AS schoolId
FROM b_commission_order co, b_service_order so, tb_user u
WHERE co.service_order_id = so.id AND so.user_id = u.id

    AND so.mara_id = #{maraId}


    AND so.adviser_id = #{adviserId}


    AND so.official_id = #{officialId}


    AND u.name LIKE '%${name}%'


    AND u.phone = #{phone}


    AND u.wechat_username = #{wechatUsername}


    AND so.school_id = #{schoolId}


    AND so.is_settle = #{isSettle}


    AND so.state = #{state}

ORDER BY co.gmt_create DESC
LIMIT #{offset}, #{rows};
