mapper.select:
SELECT count(*)
FROM b_commission_order co, b_service_order so, tb_user u, tb_adviser a, b_applicant ap
WHERE co.service_order_id = so.id AND co.user_id = u.id AND a.id = co.adviser_id AND ap.id = so.applicant_id

    AND co.id = #{id}


    AND a.region_id IN

        #{regionIdList}



    AND so.mara_id = #{maraId}


    AND co.adviser_id = #{adviserId}


    AND co.official_id = #{officialId}


    AND co.user_id = #{userId}


    AND u.name LIKE '%${name}%'


    AND (ap.surname LIKE '%${applicantName}%' OR ap.firstname LIKE '%${applicantName}%' OR CONCAT(ap.firstname,
    ' ', ap.surname) = #{applicantName})


     AND (co.state="PENDING" OR co.state="WAIT") 


     AND (co.state="COMPLETE" OR co.state="REVIEW") 


    AND u.phone = #{phone}


    AND u.wechat_username = #{wechatUsername}


    AND co.school_id IN (SELECT id FROM b_school WHERE name IN (SELECT name FROM b_school WHERE id =
    #{schoolId}))


    AND co.is_settle = #{isSettle}


    AND co.state IN

        #{stateList}



    AND co.commission_state IN

        #{commissionStateList}



     AND kj_approval_date > #{startKjApprovalDate} AND kj_approval_date < #{endKjApprovalDate} 


     AND co.gmt_create > #{startDate} AND co.gmt_create < #{endDate} 


     AND invoice_create > #{startInvoiceCreate} AND invoice_create < #{endInvoiceCreate} 


    AND (co.commission_state = 'YZY' OR (co.commission_state = 'YJY' AND co.is_settle = false))

;

SELECT count(*)
FROM b_commission_order
WHERE school_id = #{schoolId};

SELECT count(*)
FROM b_commission_order
WHERE service_order_id = #{serviceOrderId}

    AND code != #{code}

;

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.commission_state AS commissionState, co.kj_approval_date AS kjApprovalDate, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.course_id AS courseId, co.school_institution_location_id AS schoolInstitutionLocationId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, co.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, co.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, co.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, co.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, co.school_payment_amount AS schoolPaymentAmount, co.school_payment_date AS schoolPaymentDate, co.invoice_number AS invoiceNumber, co.start_date AS startDate, co.end_date AS endDate, co.commission, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.sure_expect_amount AS sureExpectAmount, co.currency, co.exchange_rate AS exchangeRate, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.zy_date AS zyDate, co.bank_check AS bankCheck, co.is_checked AS isChecked, co.remarks, co.refuse_reason AS refuseReason, co.verify_code AS verifyCode, co.bank_date AS bankDate, co.is_close AS isClose, co.invoice_create AS invoiceCreate, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId, so.institution_trading_name AS institutionTradingName, so.applicant_id AS applicantId, CONCAT(co.visa_status, '-', co.visa_status_sub) AS visaStatus, co.visa_certificate AS visaCertificate FROM b_commission_order co, b_service_order so, tb_user u, tb_adviser a, b_applicant ap WHERE co.service_order_id = so.id AND co.user_id = u.id AND a.id = co.adviser_id AND ap.id = so.applicant_id
AND co.id = #{id}
AND a.region_id IN
#{regionIdList}
AND so.mara_id = #{maraId}
AND co.adviser_id = #{adviserId}
AND co.official_id = #{officialId}
AND co.user_id = #{userId}
AND u.name LIKE '%${name}%'
AND (ap.surname LIKE '%${applicantName}%' OR ap.firstname LIKE '%${applicantName}%' OR CONCAT(ap.firstname, ' ', ap.surname) = #{applicantName})
AND u.phone = #{phone}
AND u.wechat_username = #{wechatUsername}
AND co.school_institution_location_id IN (SELECT id FROM b_school_institution_location WHERE provider_id = #{schoolId})
AND co.is_settle = #{isSettle}
AND co.state IN
#{stateList}
AND (co.state="PENDING" OR co.state="WAIT")
AND (co.state="COMPLETE" OR co.state="REVIEW")
AND co.commission_state IN
#{commissionStateList}
AND co.kj_approval_date > #{startKjApprovalDate} AND co.kj_approval_date < #{endKjApprovalDate}
AND co.gmt_create > #{startDate} AND co.gmt_create < #{endDate}
AND invoice_create > #{startInvoiceCreate} AND invoice_create < #{endInvoiceCreate}
AND (co.commission_state = 'YZY' OR (co.commission_state = 'YJY' AND co.is_settle = false))
${orderBy} LIMIT #{offset}, #{rows};

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.commission_state AS commissionState, co.kj_approval_date AS kjApprovalDate, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.course_id AS courseId, co.school_institution_location_id AS schoolInstitutionLocationId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, co.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, co.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, co.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, co.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, co.school_payment_amount AS schoolPaymentAmount, co.school_payment_date AS schoolPaymentDate, co.invoice_number AS invoiceNumber, co.start_date AS startDate, co.end_date AS endDate, co.commission, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.sure_expect_amount AS sureExpectAmount, co.currency, co.exchange_rate AS exchangeRate, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.zy_date AS zyDate, co.bank_check AS bankCheck, co.is_checked AS isChecked, co.remarks, co.refuse_reason AS refuseReason, co.verify_code AS verifyCode, co.bank_date AS bankDate, co.is_close AS isClose, co.invoice_create AS invoiceCreate, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId, so.institution_trading_name AS institutionTradingName, so.applicant_id AS applicantId FROM b_commission_order co, b_service_order so, b_school bs, tb_user tbu WHERE co.service_order_id = so.id AND co.school_id = bs.id AND co.user_id = tbu.id
AND co.gmt_create > #{startDate} AND co.gmt_create < #{endDate}
AND bs.name = #{schoolName}
ORDER BY co.gmt_create DESC;

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.commission_state AS commissionState, co.kj_approval_date AS kjApprovalDate, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.course_id AS courseId, co.school_institution_location_id AS schoolInstitutionLocationId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, co.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, co.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, co.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, co.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, co.school_payment_amount AS schoolPaymentAmount, co.school_payment_date AS schoolPaymentDate, co.invoice_number AS invoiceNumber, co.start_date AS startDate, co.end_date AS endDate, co.commission, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.sure_expect_amount AS sureExpectAmount, co.currency, co.exchange_rate AS exchangeRate, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.zy_date AS zyDate, co.bank_check AS bankCheck, co.is_checked AS isChecked, co.remarks, co.refuse_reason AS refuseReason, co.verify_code AS verifyCode, co.bank_date AS bankDate, co.is_close AS isClose, co.invoice_create AS invoiceCreate, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId, so.institution_trading_name AS institutionTradingName, so.applicant_id AS applicantId FROM b_commission_order co, b_service_order so, tb_user tbu WHERE co.service_order_id = so.id AND co.user_id = tbu.id AND DATE_FORMAT(co.installment_due_date, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m')
AND (co.state="PENDING" OR co.state="WAIT")
AND co.adviser_id = #{adviserId}
AND co.official_id = #{officialId}
ORDER BY co.installment_due_date ;

SELECT
DATE_FORMAT(co.school_payment_date, '%Y-%m') AS date,
DATE_FORMAT(co.school_payment_date, '%Y-%m-%d') AS date,
DATE_FORMAT(co.kj_approval_date, '%Y-%m') AS date,
DATE_FORMAT(co.kj_approval_date, '%Y') AS date,
DATE_FORMAT(co.kj_approval_date, '%Y-%m-%d') AS date,
r.id AS regionId, r.name AS area, a.id AS adviserId, a.name AS consultant, #IFNULL(sum(IF(co.currency='CNY',co.expect_amount/co.exchange_rate,co.expect_amount)),0.00) AS deductionCommission, SUM(IF(co.is_settle = true, CASE IFNULL(IF(co.currency='CNY',co.sure_expect_amount/co.exchange_rate,co.sure_expect_amount), 0) WHEN 0 THEN IFNULL(IF(co.currency='CNY',co.expect_amount/co.expect_amount,co.expect_amount), 0) END, 0)) AS deductionCommission, SUM(IF(co.is_settle = false, IF(co.currency='CNY',co.expect_amount/co.expect_amount,co.expect_amount), 0)) AS claimCommission, SUM(IF(co.commission_state = 'YZY', IF(co.currency='CNY',co.sure_expect_amount/co.exchange_rate,co.sure_expect_amount), 0)) AS claimedCommission, SUM(IF(co.is_settle, 0 , IFNULL(IF(co.currency='CNY',co.school_payment_amount/co.exchange_rate,co.school_payment_amount) , 0) - IFNULL(IF(co.currency='CNY',co.expect_amount/co.exchange_rate,co.expect_amount) , 0))) AS adjustments FROM tb_adviser a, tb_region r, b_commission_order co LEFT JOIN b_refund rf ON rf.commission_order_id = co.id AND rf.state = 'PAID' WHERE a.region_id = r.id AND co.adviser_id = a.id AND co.state IN ('REVIEW', 'FINISH', 'COMPLETE', 'CLOSE')
AND co.school_payment_date > #{startDate} AND co.school_payment_date <= #{endDate}
AND co.kj_approval_date > #{startDate} AND co.kj_approval_date <= #{endDate}
AND r.id = #{regionId}
AND a.id = #{adviserId}
AND a.id IN
#{aid}
GROUP BY
DATE_FORMAT(co.school_payment_date, '%Y-%m'),
DATE_FORMAT(co.school_payment_date, '%Y-%m-%d'),
DATE_FORMAT(co.kj_approval_date, '%Y-%m'),
DATE_FORMAT(co.kj_approval_date, '%Y'),
DATE_FORMAT(co.kj_approval_date, '%Y-%m-%d'),
co.adviser_id ORDER BY co.kj_approval_date DESC;

SELECT sum(tuition_fee)
FROM b_commission_order
WHERE school_id = #{schoolId};

mapper.delete:
delete b_commission_order
from b_commission_order
where b_commission_order.service_order_id =#{id, jdbcType=INTEGER} and b_commission_order.installment_num=#{installmentNum, jdbcType=INTEGER};

