mapper.update:
UPDATE b_commission_order 
SET gmt_modify = now()

    , state = #{state}


    , commission_state = #{commissionState}


    , kj_approval_date = #{kjApprovalDate}


    , is_settle = #{isSettle}


    , is_deposit_user = #{isDepositUser}


    , school_id = #{schoolId}


    , student_code = #{studentCode}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , is_studying = #{isStudying}


    , installment_due_date = #{installmentDueDate}


    , payment_voucher_image_url_1 = #{paymentVoucherImageUrl1}


    , payment_voucher_image_url_2 = #{paymentVoucherImageUrl2}


    , payment_voucher_image_url_3 = #{paymentVoucherImageUrl3}


    , payment_voucher_image_url_4 = #{paymentVoucherImageUrl4}


    , payment_voucher_image_url_5 = #{paymentVoucherImageUrl5}


    , school_payment_amount = #{schoolPaymentAmount}


    , school_payment_date = #{schoolPaymentDate}


    , invoice_number = #{invoiceNumber}


    , start_date = #{startDate}


    , end_date = #{endDate}


    , commission = #{commission}


    , tuition_fee = #{tuitionFee}


    , per_term_tuition_fee = #{perTermTuitionFee}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , per_amount = #{perAmount}


    , amount = #{amount}


    , expect_amount = #{expectAmount}


    , sure_expect_amount = #{sureExpectAmount}


    , discount = #{discount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , bonus_date = #{bonusDate}


    , zy_date = #{zyDate}


    , bank_check = #{bankCheck}


    , is_checked = #{isChecked}


    , remarks = #{remarks}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_commission_order co, b_service_order so, tb_user u
WHERE co.service_order_id = so.id AND co.user_id = u.id

    AND so.mara_id = #{maraId}


    AND co.adviser_id = #{adviserId}


    AND co.official_id = #{officialId}


    AND u.name LIKE '%${name}%'


    AND u.phone = #{phone}


    AND u.wechat_username = #{wechatUsername}


    AND co.school_id IN (SELECT id FROM b_school WHERE name IN (SELECT name FROM b_school WHERE id = #{schoolId}))


    AND co.is_settle = #{isSettle}


    AND co.state IN

        #{stateList}



    AND co.commission_state IN

        #{commissionStateList}



     AND kj_approval_date > #{startKjApprovalDate} AND kj_approval_date < #{endKjApprovalDate} 


    AND (co.commission_state = 'YZY' OR (co.commission_state = 'YJY' AND co.is_settle = false))

;

SELECT count(*)
FROM b_commission_order
WHERE school_id = #{schoolId};

SELECT count(*)
FROM b_commission_order
WHERE service_order_id = #{serviceOrderId}

    AND code != #{code}

;

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.commission_state AS commissionState, co.kj_approval_date AS kjApprovalDate, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, co.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, co.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, co.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, co.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, co.school_payment_amount AS schoolPaymentAmount, co.school_payment_date AS schoolPaymentDate, co.invoice_number AS invoiceNumber, co.start_date AS startDate, co.end_date AS endDate, co.commission, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.sure_expect_amount AS sureExpectAmount, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.zy_date AS zyDate, co.bank_check AS bankCheck, co.is_checked AS isChecked, co.remarks, co.is_close AS isClose, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId FROM b_commission_order co, b_service_order so, tb_user u WHERE co.service_order_id = so.id AND co.user_id = u.id
AND so.mara_id = #{maraId}
AND co.adviser_id = #{adviserId}
AND co.official_id = #{officialId}
AND u.name LIKE '%${name}%'
AND u.phone = #{phone}
AND u.wechat_username = #{wechatUsername}
AND co.school_id IN (SELECT id FROM b_school WHERE name IN (SELECT name FROM b_school WHERE id = #{schoolId}))
AND co.is_settle = #{isSettle}
AND co.state IN
#{stateList}
AND co.commission_state IN
#{commissionStateList}
AND co.kj_approval_date > #{startKjApprovalDate} AND co.kj_approval_date < #{endKjApprovalDate}
AND (co.commission_state = 'YZY' OR (co.commission_state = 'YJY' AND co.is_settle = false))
ORDER BY co.gmt_create DESC, co.installment_num ASC LIMIT #{offset}, #{rows};

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.commission_state AS commissionState, co.kj_approval_date AS kjApprovalDate, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, co.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, co.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, co.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, co.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, co.school_payment_amount AS schoolPaymentAmount, co.school_payment_date AS schoolPaymentDate, co.invoice_number AS invoiceNumber, co.start_date AS startDate, co.end_date AS endDate, co.commission, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.sure_expect_amount AS sureExpectAmount, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.zy_date AS zyDate, co.bank_check AS bankCheck, co.is_checked AS isChecked, co.remarks, co.is_close AS isClose, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId FROM b_commission_order co, b_service_order so, b_school bs, tb_user tbu WHERE co.service_order_id = so.id AND co.school_id = bs.id AND co.user_id = tbu.id
AND co.gmt_create > #{startDate} AND co.gmt_create < #{endDate}
AND bs.name = #{schoolName}
ORDER BY co.gmt_create DESC;

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.commission_state AS commissionState, co.kj_approval_date AS kjApprovalDate, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, co.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, co.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, co.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, co.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, co.school_payment_amount AS schoolPaymentAmount, co.school_payment_date AS schoolPaymentDate, co.invoice_number AS invoiceNumber, co.start_date AS startDate, co.end_date AS endDate, co.commission, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.sure_expect_amount AS sureExpectAmount, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.zy_date AS zyDate, co.bank_check AS bankCheck, co.is_checked AS isChecked, co.remarks, co.is_close AS isClose, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId FROM b_commission_order co, b_service_order so, b_school bs, tb_user tbu WHERE co.service_order_id = so.id AND co.school_id = bs.id AND co.user_id = tbu.id AND DATE_FORMAT(co.installment_due_date, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m') AND co.bonus != 0.00
AND co.adviser_id = #{adviserId}
AND co.official_id = #{officialId}
ORDER BY co.installment_due_date LIMIT 10;

SELECT sum(tuition_fee)
FROM b_commission_order
WHERE school_id = #{schoolId};
