mapper.update:
UPDATE b_commission_order 
SET gmt_modify = now()

    , service_order_id = #{serviceOrderId}


    , installment = #{installment}


    , start_date = #{startDate}


    , end_date = #{endDate}


    , tuition_fee = #{tuitionFee}


    , per_term_tuition_fee = #{perTermTuitionFee}


    , remarks = #{remarks}

WHERE id = #{id};
