mapper.update:
UPDATE b_commission_order 
SET gmt_modify = now()

    , state = #{state}


    , commission_state = #{commissionState}


    , is_settle = #{isSettle}


    , is_deposit_user = #{isDepositUser}


    , school_id = #{schoolId}


    , student_code = #{studentCode}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , is_studying = #{isStudying}


    , installment_due_date = #{installmentDueDate}


    , payment_voucher_image_url_1 = #{paymentVoucherImageUrl1}


    , payment_voucher_image_url_2 = #{paymentVoucherImageUrl2}


    , school_payment_amount = #{schoolPaymentAmount}


    , school_payment_date = #{schoolPaymentDate}


    , invoice_number = #{invoiceNumber}


    , start_date = #{startDate}


    , end_date = #{endDate}


    , commission = #{commission}


    , tuition_fee = #{tuitionFee}


    , per_term_tuition_fee = #{perTermTuitionFee}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , per_amount = #{perAmount}


    , amount = #{amount}


    , expect_amount = #{expectAmount}


    , discount = #{discount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , remarks = #{remarks}

WHERE id = #{id};

mapper.select:
        SELECT count(*)
        FROM b_commission_order co, b_service_order so, tb_user u
        WHERE co.service_order_id = so.id AND co.user_id = u.id

            AND so.mara_id = #{maraId}


            AND co.adviser_id = #{adviserId}


            AND co.official_id = #{officialId}


            AND u.name LIKE '%${name}%'


            AND u.phone = #{phone}


            AND u.wechat_username = #{wechatUsername}


            AND co.school_id = #{schoolId}


            AND co.is_settle = #{isSettle}


			AND co.state IN

				#{stateList}



			AND co.commission_state IN

				#{commissionStateList}


        ;

SELECT count(*)
FROM b_commission_order bco, b_school bs
WHERE bco.school_id = bs.id AND bs.name = #{schoolName};

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, co.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, co.school_payment_amount AS schoolPaymentAmount, co.school_payment_date AS schoolPaymentDate, co.invoice_number AS invoiceNumber, co.start_date AS startDate, co.end_date AS endDate, co.commission, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.remarks, co.is_close AS isClose, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId FROM b_commission_order co, b_service_order so, tb_user u WHERE co.service_order_id = so.id AND co.user_id = u.id
AND so.mara_id = #{maraId}
AND co.adviser_id = #{adviserId}
AND co.official_id = #{officialId}
AND u.name LIKE '%${name}%'
AND u.phone = #{phone}
AND u.wechat_username = #{wechatUsername}
AND co.school_id = #{schoolId}
AND co.is_settle = #{isSettle}
AND co.state IN
#{stateList}
AND co.commission_state IN
#{commissionStateList}
ORDER BY co.gmt_create, co.installment_num ASC LIMIT #{offset}, #{rows};

SELECT bco.id, bco.gmt_create AS gmtCreate, bco.code, bco.service_order_id AS serviceOrderId, bco.state, bco.commission_state AS commissionState, bco.is_settle AS isSettle, bco.is_deposit_user AS isDepositUser, bco.school_id AS schoolId, bco.student_code AS studentCode, bco.user_id AS userId, bco.adviser_id AS adviserId, bco.official_id AS officialId, bco.is_studying AS isStudying, bco.installment_num AS installmentNum, bco.installment, bco.installment_due_date AS installmentDueDate, bco.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, bco.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, bco.school_payment_amount AS schoolPaymentAmount, bco.school_payment_date AS schoolPaymentDate, bco.invoice_number AS invoiceNumber, bco.start_date AS startDate, bco.end_date AS endDate, bco.commission, bco.tuition_fee AS tuitionFee, bco.per_term_tuition_fee AS perTermTuitionFee, bco.receive_type_id AS receiveTypeId, bco.receive_date AS receiveDate, bco.per_amount AS perAmount, bco.amount, bco.expect_amount AS expectAmount, bco.discount, bco.gst, bco.deduct_gst AS deductGst, bco.bonus, bco.bonus_date AS bonusDate, bco.remarks, bco.is_close AS isClose FROM b_commission_order bco, b_school bs, tb_user tbu WHERE bco.school_id = bs.id AND bbs.user_id = tbu.id
AND bco.gmt_create > #{startDate} AND bco.gmt_create < #{endDate}
AND bs.name = #{schoolName}
ORDER BY bco.gmt_create DESC;

SELECT sum(bco.tuition_fee)
FROM b_commission_order bco, b_school bs
WHERE bco.school_id = bs.id AND bs.name = #{schoolName};
