mapper.update:
UPDATE b_commission_order 
SET gmt_modify = now()

    , state = #{state}


    , is_settle = #{isSettle}


    , is_deposit_user = #{isDepositUser}


    , school_id = #{schoolId}


    , student_code = #{studentCode}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , is_studying = #{isStudying}


    , installment_due_date = #{installmentDueDate}


    , start_date = #{startDate}


    , end_date = #{endDate}


    , tuition_fee = #{tuitionFee}


    , per_term_tuition_fee = #{perTermTuitionFee}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , per_amount = #{perAmount}


    , amount = #{amount}


    , expect_amount = #{expectAmount}


    , discount = #{discount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , remarks = #{remarks}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_commission_order co, b_service_order so, tb_user u
WHERE co.service_order_id = so.id AND co.user_id = u.id

    AND so.mara_id = #{maraId}


    AND so.adviser_id = #{adviserId}


    AND so.official_id = #{officialId}


    AND u.name LIKE '%${name}%'


    AND u.phone = #{phone}


    AND u.wechat_username = #{wechatUsername}


    AND so.school_id = #{schoolId}


    AND so.is_settle = #{isSettle}


    AND so.state = #{state}

;

SELECT co.id, co.gmt_create AS gmtCreate, co.code, co.service_order_id AS serviceOrderId, co.state, co.is_settle AS isSettle, co.is_deposit_user AS isDepositUser, co.school_id AS schoolId, co.student_code AS studentCode, co.user_id AS userId, co.adviser_id AS adviserId, co.official_id AS officialId, co.is_studying AS isStudying, co.installment_num AS installmentNum, co.installment, co.installment_due_date AS installmentDueDate, co.start_date AS startDate, co.end_date AS endDate, co.tuition_fee AS tuitionFee, co.per_term_tuition_fee AS perTermTuitionFee, co.receive_type_id AS receiveTypeId, co.receive_date AS receiveDate, co.per_amount AS perAmount, co.amount, co.expect_amount AS expectAmount, co.discount, co.gst, co.deduct_gst AS deductGst, co.bonus, co.bonus_date AS bonusDate, co.remarks, co.is_close AS isClose, so.id AS serviceOrderId, so.gmt_create AS signingDate, so.subagency_id AS subagencyId, so.service_id AS serviceId FROM b_commission_order co, b_service_order so, tb_user u WHERE co.service_order_id = so.id AND co.user_id = u.id
AND so.mara_id = #{maraId}
AND co.adviser_id = #{adviserId}
AND co.official_id = #{officialId}
AND u.name LIKE '%${name}%'
AND u.phone = #{phone}
AND u.wechat_username = #{wechatUsername}
AND co.school_id = #{schoolId}
AND co.is_settle = #{isSettle}
AND co.state = #{state}
ORDER BY co.gmt_create, co.installment_num ASC LIMIT #{offset}, #{rows};
