mapper.update:
UPDATE b_knowledge 
SET gmt_modify = now()

    , title = #{title}


    , content = #{content}


    , knowledge_menu_id = #{knowledgeMenuId}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_knowledge
WHERE 1 = 1

    AND knowledge_menu_id = #{knowledgeMenuId}


    AND (title LIKE '%${keyword}%' OR content LIKE '%${keyword}%')

;

SELECT id, gmt_create AS gmtCreate, title, content, knowledge_menu_id AS knowledgeMenuId FROM b_knowledge WHERE 1 = 1
AND knowledge_menu_id = #{knowledgeMenuId}
AND (title LIKE '%${keyword}%' OR content LIKE '%${keyword}%')
ORDER BY id ASC LIMIT #{offset}, #{rows};
