mapper.select:
SELECT id, gmt_create AS gmtCreate, gmt_modify AS gmtModify, code , mail, title, content, send_date AS sendDate, service_order_id AS serviceOrderId, visa_id AS visaId, commission_order_id AS commissionOrderId, adviser_id AS adviserId, offcial_id AS offcialId, kj_id AS kjId, user_id AS userId from b_mail_remind
AND adviser_id = #{adviserId}
AND offcial_id = #{offcialId}
AND kj_id = #{kjId}
AND service_order_id = #{serviceOrderId}
AND visa_id = #{visaId}
AND commission_order_id = #{commissionOrderId}
AND user_id = #{userId}
AND DATE_FORMAT(send_date,'%Y%m%d') = DATE_FORMAT(NOW(),'%Y%m%d')
AND send_date >= NOW()
ORDER BY send_date DESC LIMIT 20;

mapper.delete:
DELETE FROM b_mail_remind WHERE id = #{id}

    AND  adviser_id = #{adviserId}


    AND  offcial_id = #{offcialId}

;

mapper.update:
UPDATE b_mail_remind
SET gmt_modify = NOW()

    , send_date = #{sendDate}


    , content = #{content}


    , is_send = #{isSend}

WHERE id = #{id};
