mapper.select:
SELECT id, gmt_create AS gmtCreate, gmt_modify AS gmtModify, code , mail, title, content, send_date AS sendDate, service_order_id AS serviceOrderId, visa_id AS visaId, commission_order_id AS commissionOrderId, adviser_id AS adviserId, offcial_id AS offcialId from b_mail_remind
AND adviser_id = #{adviserId}
AND offcial_id = #{offcialId}
AND service_order_id = #{serviceOrderId}
AND visa_id = #{visaId}
AND commission_order_id = #{commissionOrderId}
ORDER BY send_date ASC;

mapper.delete:
DELETE FROM b_mail_remind WHERE id = #{id}

    AND  adviser_id = #{adviserId}


    AND  offcial_id = #{offcialId}

;

mapper.update:
UPDATE b_mail_remind
SET gmt_modify = NOW()

    , send_date = #{sendDate}


    , content = #{content}

WHERE id = #{id};
