mapper.update:
UPDATE b_official 
SET gmt_modify = now()

    , name = #{name}


    , phone = #{phone}


    , email = #{email}


    , state = #{state}


    , image_url = #{imageUrl}


    , region_id = #{regionId}


    , specialty = #{specialty}


    , work_state = #{workState}


    , grade_id = #{gradeId}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_official
WHERE 1 = 1

    AND name = #{name}


    AND region_id = #{regionId}

;

SELECT id, name, phone, email, state, image_url AS imageUrl, region_id AS regionId, specialty AS specialty, work_state AS workState, grade_id AS gradeId FROM b_official WHERE 1 = 1
AND name = #{name}
AND region_id = #{regionId}
ORDER BY name LIMIT #{offset}, #{rows};
