mapper.select:
SELECT id, gmt_create AS gmtCreate, gmt_modify AS gmtModify, state, type, visa_id AS visaId, commission_order_id AS commissionOrderId, receive_date AS receiveDate, received, payment_voucher_image_url AS paymentVoucherImageUrl, refund_voucher_image_url AS refundVoucherImageUrl, refund_detail_id AS refundDetailId, refund_detail AS refundDetail, currency_type AS currencyType, amount_name AS amountName, bank_name AS bankName, bsb, remarks FROM b_order_refund WHERE 1=1
AND type = #{type}
AND visa_id = #{visaId}
AND commission_order_id = #{commissionOrderId}
AND state = #{state}
ORDER BY gmt_create DESC;

mapper.update:
UPDATE b_order_refund 
SET gmt_modify = now()

    , state = #{state}


    , receive_date = #{receiveDate}


    , received = #{received}


    , payment_voucher_image_url = #{paymentVoucherImageUrl}


    , refund_voucher_image_url = #{refundVoucherImageUrl}


    , refund_detail_id = #{refundDetailId}


    , refund_detail = #{refundDetail}


    , currency_type = #{currencyType}


    , amount_name = #{amountName}


    , bank_name = #{bankName}


    , bsb = #{bsb}


    , remarks = #{remarks}

WHERE id = #{id};
