mapper.select:
SELECT count(*)
FROM b_qywx_external_user
WHERE 1 = 1

    AND gmt_create = #{gmtCreate}


    AND state = #{state}

;

SELECT gmt_create AS gmtCreate, name, gender, type, avatar, external_userid AS externalUserid, state, user_id AS userId, applicant_id AS applicantId, phone, email, wechat_username AS wechatUsername, weibo_username AS weiboUsername, qq FROM b_qywx_external_user WHERE 1 = 1
AND gmt_create = #{gmtCreate}
AND state = #{state}
ORDER BY gmt_create DESC LIMIT #{offset}, #{rows};

SELECT
    external_userid AS externalUserid,
    key,
    value
FROM b_qywx_external_user_description
WHERE 1 = 1

    AND external_userid = #{externalUserid}


    AND key = #{key}

;
