mapper.update:
UPDATE b_qywx_external_user 
SET gmt_modify = now()

    , state = #{state}


    , user_id = #{userId}


    , applicant_id = #{applicantId}


    , gender = #{gender}


    , type = #{type}


    , phone = #{phone}


    , email = #{email}


    , wechat_username = #{wechatUsername}


    , weibo_username = #{weiboUsername}


    , qq = #{qq}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_qywx_external_user
WHERE 1 = 1

    AND adviser_id = #{adviserId}


    AND state = #{state}


     AND gmt_create > #{startDate} AND gmt_create < #{endDate} 

;

SELECT id, gmt_create AS gmtCreate, union_id AS unionId, create_time AS createTime, adviser_id AS adviserId, name, gender, type, avatar, external_userid AS externalUserid, state, user_id AS userId, applicant_id AS applicantId, phone, email, wechat_username AS wechatUsername, weibo_username AS weiboUsername, qq FROM b_qywx_external_user WHERE 1 = 1
AND adviser_id = #{adviserId}
AND state = #{state}
AND create_time > #{startDate} AND create_time < #{endDate}
ORDER BY gmt_create DESC LIMIT #{offset}, #{rows};

mapper.update:
UPDATE b_qywx_external_user_description 
SET gmt_modify = now()

    , qywx_value = #{qywxValue}

WHERE id = #{id};

mapper.select:
SELECT
    id,
    qywx_external_userid AS qywxExternalUserId,
    qywx_key AS qywxKey,
    qywx_value AS qywxValue
FROM b_qywx_external_user_description
WHERE 1 = 1

    AND qywx_external_userid = #{qywxExternalUserId}


    AND qywx_key = #{qywxKey}

;

SELECT id, qywx_external_userid AS qywxExternalUserId, qywx_key AS qywxKey, qywx_value AS qywxValue FROM b_qywx_external_user_description WHERE 1 = 1
AND applicant_id = #{applicantId}
;
