mapper.select:
SELECT r.id, r.gmt_create AS gmtCreate, r.gmt_modify AS gmtModify, r.state, r.type, r.visa_id AS visaId, r.commission_order_id AS commissionOrderId, r.user_id AS userId, u.name AS userName, r.adviser_id AS adviserId, a.name AS adviserName, r.mara_id AS maraId, r.official_id AS officialId, o.name AS officialName, r.school_id AS schoolId, r.course_id AS courseId, r.receive_date AS receiveDate, r.received, r.amount, r.payment_voucher_image_url AS paymentVoucherImageUrl, r.refund_voucher_image_url AS refundVoucherImageUrl, r.refund_detail_id AS refundDetailId, r.refund_detail AS refundDetail, r.currency_type AS currencyType, r.account_name AS account_name, r.bank_name AS bankName, r.bsb, r.remarks FROM b_refund r, tb_user u, tb_adviser a, b_official o WHERE r.user_id = u.id AND r.adviser_id = a.id AND r.b_official = o.id
AND r.type = #{type}
AND r.state = #{state}
ORDER BY r.gmt_create DESC;

mapper.update:
UPDATE b_refund 
SET gmt_modify = now()

    , state = #{state}


    , user_id = #{userId}


    , adviser_id = #{adviserId}


    , mara_id = #{maraId}


    , official_id = #{officialId}


    , school_id = #{schoolId}


    , course_id = #{courseId}


    , receive_date = #{receiveDate}


    , received = #{received}


    , amount = #{amount}


    , payment_voucher_image_url = #{paymentVoucherImageUrl}


    , refund_voucher_image_url = #{refundVoucherImageUrl}


    , refund_detail_id = #{refundDetailId}


    , refund_detail = #{refundDetail}


    , currency_type = #{currencyType}


    , account_name = #{accountName}


    , bank_name = #{bankName}


    , bsb = #{bsb}


    , remarks = #{remarks}

WHERE id = #{id};
