mapper.update:
UPDATE b_refund 
SET gmt_modify = now()

    , handling_date = #{handlingDate}


    , user_id = #{userId}


    , name = #{name}


    , receive_type_id = #{receiveTypeId}


    , amount = #{amount}


    , pre_refund_amount = #{preRefundAmount}


    , bank_name = #{bankName}


    , bank_account = #{bankAccount}


    , bsb = #{bsb}


    , refund_date = #{refundDate}


    , refund_amount = #{refundAmount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , refund = #{refund}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , is_close = #{isClose}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_refund br, b_official bo, tb_user tbu
WHERE br.official_id = bo.id AND br.user_id = tbu.id

    AND tbu.name LIKE '%${keyword}%'


     AND br.handling_date > #{startHandlingDate} AND br.handling_date < #{endHandlingDate} 


     AND br.refund_date > #{startDate} AND br.refund_date < #{endDate} 


    AND br.adviser_id = #{adviserId}


    AND br.official_id = #{officialId}

;

SELECT br.id, br.gmt_create AS gmtCreate, br.handling_date AS handlingDate, br.user_id AS userId, br.name, br.receive_type_id AS receiveTypeId, br.amount, br.pre_refund_amount AS preRefundAmount, br.bank_name AS bankName, br.bank_account AS bankAccount, br.bsb, br.refund_date AS refundDate, br.refund_amount AS refundAmount, br.gst, br.deduct_gst AS deductGst, br.refund, br.adviser_id AS adviserId, br.official_id AS officialId, br.is_close AS isClose, bo.name AS officialName, tbu.name AS userName, tbu.birthday AS birthday, tbu.phone AS phone FROM b_refund br, b_official bo, tb_user tbu WHERE br.official_id = bo.id AND br.user_id = tbu.id
AND tbu.name LIKE '%${keyword}%'
AND br.handling_date > #{startHandlingDate} AND br.handling_date < #{endHandlingDate}
AND br.refund_date > #{startDate} AND br.refund_date < #{endDate}
AND br.adviser_id = #{adviserId}
AND br.official_id = #{officialId}
ORDER BY br.gmt_create DESC LIMIT #{offset}, #{rows};
