mapper.select:
        SELECT
            count(*)
        FROM tb_user u, tb_adviser a, tb_region re, b_refund r 
        WHERE r.user_id = u.id AND r.adviser_id = a.id AND re.id = a.region_id

			AND r.type = #{type}


			AND r.state = #{state}


            AND r.visa_id = #{visaId}


            AND r.commission_order_id = #{commissionOrderId}


            AND re.id = #{regionId}


            AND r.adviser_id = #{adviserId}


            AND a.name like '%${adviserName}%'


             AND r.gmt_create > #{startDate} AND r.gmt_create < #{endDate} 

        ;

SELECT r.id, r.gmt_create AS gmtCreate, r.gmt_modify AS gmtModify, r.reviewed_date AS reviewedDate, r.completed_date AS completedDate, r.state, r.type, r.visa_id AS visaId, r.commission_order_id AS commissionOrderId, r.user_id AS userId, r.applicant_id AS applicantId, u.name AS userName, r.adviser_id AS adviserId, a.name AS adviserName, re.name AS adviserRegionName, r.mara_id AS maraId, r.official_id AS officialId, o.name AS officialName, r.school_id AS schoolId, r.course_id AS courseId, r.receive_date AS receiveDate, r.received, r.amount, r.payment_voucher_image_url AS paymentVoucherImageUrl, r.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, r.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, r.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, r.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, r.refund_voucher_image_url AS refundVoucherImageUrl, r.refund_detail_id AS refundDetailId, r.refund_detail AS refundDetail, r.currency_type AS currencyType, r.exchange_rate AS exchangeRate, r.account_name AS accountName, r.bank_name AS bankName, r.bsb, r.rmb_remarks AS rmbRemarks, r.reason AS reason, r.remarks, r.note FROM tb_user u, tb_adviser a, tb_region re, b_refund r left join b_official o ON r.official_id = o.id WHERE r.user_id = u.id AND r.adviser_id = a.id AND re.id = a.region_id
AND r.type = #{type}
AND r.state = #{state}
AND r.visa_id = #{visaId}
AND r.commission_order_id = #{commissionOrderId}
AND re.id = #{regionId}
AND r.adviser_id = #{adviserId}
AND a.name like '%${adviserName}%'
AND r.gmt_create > #{startDate} AND r.gmt_create < #{endDate}
ORDER BY r.gmt_create DESC LIMIT #{offset}, #{rows};

mapper.update:
UPDATE b_refund 
SET gmt_modify = now()

    , state = #{state}


    , user_id = #{userId}


    , applicant_id = #{applicantId}


    , adviser_id = #{adviserId}


    , mara_id = #{maraId}


    , official_id = #{officialId}


    , school_id = #{schoolId}


    , course_id = #{courseId}


    , receive_date = #{receiveDate}


    , received = #{received}


    , reviewed_date = #{reviewedDate}


    , completed_date = #{completedDate}


    , amount = #{amount}


    , payment_voucher_image_url = #{paymentVoucherImageUrl}


    , payment_voucher_image_url_2 = #{paymentVoucherImageUrl2}


    , payment_voucher_image_url_3 = #{paymentVoucherImageUrl3}


    , payment_voucher_image_url_4 = #{paymentVoucherImageUrl4}


    , payment_voucher_image_url_5 = #{paymentVoucherImageUrl5}


    , refund_voucher_image_url = #{refundVoucherImageUrl}


    , refund_detail_id = #{refundDetailId}


    , refund_detail = #{refundDetail}


    , currency_type = #{currencyType}


    , account_name = #{accountName}


    , bank_name = #{bankName}


    , bsb = #{bsb}


    , rmb_remarks = #{rmbRemarks}


    , reason = #{reason}


    , remarks = #{remarks}


    , note = #{note}

WHERE id = #{id};
