mapper.select:
SELECT id, gmt_create AS gmtCreate, gmt_modify AS gmtModify, state, type, user_id AS userId, adviser_id AS adviserId, mara_id AS maraId, official_id AS officialId, school_id AS schoolId, course_id AS courseId, receive_date AS receiveDate, received, payment_voucher_image_url AS paymentVoucherImageUrl, refund_voucher_image_url AS refundVoucherImageUrl, refund_detail_id AS refundDetailId, refund_detail AS refundDetail, currency_type AS currencyType, amount_name AS amountName, bank_name AS bankName, bsb, remarks FROM b_manual_refund WHERE 1=1
AND type = #{type}
AND state = #{state}
ORDER BY gmt_create DESC;

mapper.update:
UPDATE b_manual_refund 
SET gmt_modify = now()

    , state = #{state}


    , user_id = #{userId}


    , adviser_id = #{adviserId}


    , mara_id = #{maraId}


    , official_id = #{officialId}


    , school_id = #{schoolId}


    , course_id = #{courseId}


    , receive_date = #{receiveDate}


    , received = #{received}


    , payment_voucher_image_url = #{paymentVoucherImageUrl}


    , refund_voucher_image_url = #{refundVoucherImageUrl}


    , refund_detail_id = #{refundDetailId}


    , refund_detail = #{refundDetail}


    , currency_type = #{currencyType}


    , amount_name = #{amountName}


    , bank_name = #{bankName}


    , bsb = #{bsb}


    , remarks = #{remarks}

WHERE id = #{id};
