mapper.select:
SELECT
    r.id,
    r.gmt_create AS gmtCreate,
    r.gmt_modify AS gmtModify,
    r.service_order_id AS serviceOrderId,
    r.remind_date AS remindDate,
    r.state
FROM b_remind r, b_service_order so
WHERE r.service_order_id = so.id AND r.service_order_id = #{serviceOrderId, jdbcType=INTEGER}

    AND so.adviser_id = #{adviserId}


    AND r.state = #{state}

ORDER BY r.remind_date DESC;

mapper.update:
		UPDATE b_remind
		SET gmt_modify = now()

            ,state = #{state}

		WHERE id = #{id};
