mapper.update:
UPDATE b_school_brokerage_sa
SET gmt_modify = now()

	, handling_date = #{handlingDate}


	, user_id = #{userId}


	, school_id = #{schoolId}


	, student_code = #{studentCode}


	, start_date = #{startDate}


	, end_date = #{endDate}


	, tuition_fee = #{tuitionFee}


	, first_term_tuition_fee = #{firstTermTuitionFee}


	, commission = #{commission}


	, gst = #{gst}


	, deduct_gst = #{deductGst}


	, bonus = #{bonus}


	, pay_date = #{payDate}


	, invoice_code = #{invoiceCode}


	, pay_amount = #{payAmount}


	, subagency_id = #{subagencyId}


	, adviser_id = #{adviserId}


	, official_id = #{officialId}


	, is_settle_accounts = #{isSettleAccounts}


	, is_close = #{isClose}

WHERE id = #{id};

mapper.select:
		SELECT count(*)
		FROM b_school_brokerage_sa bsbs, b_school bs, tb_user tbu
		WHERE bsbs.school_id = bs.id AND bsbs.user_id = tbu.id AND
		bsbs.is_settle_accounts = #{isSettleAccounts}

			AND tbu.name LIKE '%${keyword}%'


             AND bsbs.handling_date > #{startHandlingDate} AND bsbs.handling_date < #{endHandlingDate} 


             AND bsbs.start_date > #{startDate} AND bsbs.start_date < #{endDate} 


			AND bsbs.adviser_id = #{adviserId}


			AND bsbs.school_id = #{schoolId}


			AND bsbs.subagency_id = #{subagencyId}


			AND bsbs.user_id = #{userId}

		;

SELECT bsbs.id AS id, bsbs.gmt_create AS gmtCreate, bsbs.handling_date AS handlingDate, bsbs.user_id AS userId, bsbs.school_id AS schoolId, bsbs.student_code AS studentCode, bsbs.start_date AS startDate, bsbs.end_date AS endDate, bsbs.tuition_fee AS tuitionFee, bsbs.first_term_tuition_fee AS firstTermTuitionFee, bsbs.commission AS commission, bsbs.gst AS gst, bsbs.deduct_gst AS deductGst, bsbs.bonus AS bonus, bsbs.pay_date AS payDate, bsbs.invoice_code AS invoiceCode, bsbs.pay_amount AS payAmount, bsbs.subagency_id AS subagencyId, bsu.name AS subagencyName, bsbs.adviser_id AS adviserId, bsbs.official_id AS officialId, bsbs.is_settle_accounts AS isSettleAccounts, bsbs.is_close AS isClose, bs.name AS schoolName, bs.subject AS schoolSubject, tbu.name AS userName, tbu.birthday AS birthday, tbu.phone AS phone FROM b_school_brokerage_sa bsbs, b_school bs, tb_user tbu, b_subagency bsu WHERE bsbs.school_id = bs.id AND bsbs.user_id = tbu.id AND bsbs.subagency_id = bsu.id AND bsbs.is_settle_accounts = #{isSettleAccounts}
AND tbu.name LIKE '%${keyword}%'
AND bsbs.handling_date > #{startHandlingDate} AND bsbs.handling_date < #{endHandlingDate}
AND bsbs.start_date > #{startDate} AND bsbs.start_date < #{endDate}
AND bsbs.adviser_id = #{adviserId}
AND bsbs.school_id = #{schoolId}
AND bsbs.subagency_id = #{subagencyId}
AND bsbs.user_id = #{userId}
ORDER BY bsbs.gmt_create DESC LIMIT #{offset}, #{rows};

		select
		IFNULL(sum(bonus), 0.00) from b_school_brokerage_sa where
		DATE_FORMAT(gmt_create, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m') and
		is_close = 0

            AND bb.adviser_id = #{adviserId}

		;
