mapper.update:
UPDATE b_school 
SET gmt_modify = now()

    , name = #{name}


    , subject = #{subject}


    , country = #{country}

WHERE is_delete = 0 AND id = #{id};

mapper.select:
SELECT id, name, subject, country FROM b_school WHERE is_delete = 0
AND name LIKE '%${name}%'
AND subject LIKE '%${subject}%'
AND country = #{country}
ORDER BY gmt_create DESC;

SELECT id, name, subject, country FROM b_school WHERE is_delete = 0
AND name = #{name}
;

SELECT id, name, country FROM b_school WHERE is_delete = 0
AND name LIKE '%${name}%'
AND country = #{country}
GROUP BY name ORDER BY gmt_create DESC;

mapper.update:
UPDATE b_school 
SET gmt_modify = now(), is_delete = 1
WHERE id = #{id, jdbcType=INTEGER};

UPDATE b_school 
SET gmt_modify = now(), is_delete = 1
WHERE name = #{name};
