mapper.select:
select id, gmt_create AS gmtCreate, gmt_modify AS gmtModify, name, state, number_of_courses AS numberOfCourses, provider_id AS providerId, provider_code AS providerCode, phone from b_school_institution_location
and provider_id = #{providerId}
and provider_code = #{providerCode}
;

mapper.update:
UPDATE b_school_institution_location
SET gmt_modify = NOW()

    , name = #{name}


    , state = #{state}


    , number_of_courses = #{numberOfCourses}


    , phone = #{phone}

WHERE id = #{id};

mapper.select:
SELECT state
FROM b_school_institution_location
GROUP BY state;
