mapper.update:
UPDATE b_school_setting 
SET gmt_modify = now()

    , type = #{type}


    , start_date = #{startDate}


    , end_date = #{endDate}


    , parameters = #{parameters}

WHERE id = #{id};

mapper.select:
SELECT id, school_name AS schoolName, type, start_date AS startDate, end_date AS endDate, parameters FROM b_school_setting WHERE 1=1
AND name LIKE '%${name}%'
AND subject LIKE '%${subject}%'
AND country = #{country}
ORDER BY gmt_create DESC;
