mapper.update:
UPDATE b_service 
SET gmt_modify = now()

    , type = #{type}


    , code = #{code}


    , is_pay = #{isPay}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , receivable = #{receivable}


    , discount = #{discount}


    , received = #{received}


    , payment_times = #{paymentTimes}


    , amount = #{amount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , mara_id = #{maraId}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , remarks = #{remarks}

WHERE id = #{id};

UPDATE b_service 
SET gmt_modify = now(), is_delete = 1
WHERE id = #{id, jdbcType=INTEGER};
