mapper.update:
UPDATE b_service 
SET gmt_modify = now()

    , name = #{name}


    , code = #{code}


    , role = #{role}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_service


        AND name = #{name}


        AND is_zx = #{isZx}


        AND is_delete = 0 AND is_zx = FALSE


;

SELECT id, gmt_create AS GmtCreate, name, code, role, is_zx AS isZx, is_delete AS isDelete FROM b_service
AND name = #{name}
AND is_zx = #{isZx}
AND is_delete = 0 AND is_zx = FALSE
ORDER BY code LIMIT #{offset}, #{rows};

SELECT count(*)
FROM b_service


        AND name = #{name}


;

SELECT id, gmt_create AS GmtCreate, name, code, role, is_zx AS isZx, is_delete AS isDelete FROM b_service
AND name = #{name}
ORDER BY code LIMIT #{offset}, #{rows};

mapper.update:
