mapper.select:
SELECT id, gmt_create AS gmtCreate, admin_user_id AS adminUserId, service_order_id AS serviceOrderId, content, score, score_official_id AS scoreOfficialId FROM b_service_order_comment WHERE
service_order_id = #{serviceOrderId}
, AND score_official_id = #{scoreOfficialId}
ORDER BY gmt_create DESC;
