UPDATE b_service_order 
SET gmt_modify = now(), review_state = #{reviewState}
WHERE id = #{id};

mapper.select:
        SELECT count(*)
        FROM b_service_order so, tb_adviser a
        WHERE so.is_delete = 0 AND a.id = so.adviser_id

			AND so.type = #{type}


			AND so.state != #{excludeState}


			AND so.state IN

				#{stateList}



			AND so.review_state IN

				#{reviewStateList}



             AND so.mara_approval_date > #{startMaraApprovalDate} AND so.mara_approval_date < #{endMaraApprovalDate} 


             AND so.official_approval_date > #{startOfficialApprovalDate} AND so.official_approval_date < #{endOfficialApprovalDate} 


			AND a.region_id = #{regionId}


			AND so.user_id = #{userId}


			AND so.mara_id = #{maraId}


			AND (so.adviser_id = #{adviserId} OR so.adviser_id_2 = #{adviserId})


			AND so.official_id = #{officialId}


			AND so.parent_id = #{parentId}


			 AND (SELECT COUNT(id) FROM b_service_order_review WHERE service_order_id = so.id AND official_state IS NOT NULL) <= 0 

        ;

SELECT so.id AS id, so.gmt_create AS gmtCreate, so.gmt_modify AS gmtModify, so.code AS code, so.finish_date AS finishDate, so.type AS type, so.people_number AS peopleNumber, so.people_type AS peopleType, so.people_remarks AS peopleRemarks, so.service_id AS serviceId, so.parent_id AS parentId, so.service_package_id AS servicePackageId, so.school_id AS schoolId, so.state AS state, so.mara_approval_date AS maraApprovalDate, so.official_approval_date AS officialApprovalDate, so.is_settle AS isSettle, so.is_deposit_user AS isDepositUser, so.is_submitted AS isSubmitted, so.subagency_id AS subagencyId, so.is_pay AS isPay, so.receive_type_id AS receiveTypeId, so.receive_date AS receiveDate, so.receivable AS receivable, so.discount AS discount, so.received AS received, so.installment AS installment, so.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, so.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, so.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, so.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, so.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, so.coe_payment_voucher_image_url_1 AS coePaymentVoucherImageUrl1, so.coe_payment_voucher_image_url_2 AS coePaymentVoucherImageUrl2, so.coe_payment_voucher_image_url_3 AS coePaymentVoucherImageUrl3, so.coe_payment_voucher_image_url_4 AS coePaymentVoucherImageUrl4, so.coe_payment_voucher_image_url_5 AS coePaymentVoucherImageUrl5, so.visa_voucher_image_url AS visaVoucherImageUrl, so.per_amount AS perAmount, so.amount AS amount, so.expect_amount AS expectAmount, so.gst AS gst, so.deduct_gst AS deductGst, so.bonus AS bonus, so.user_id AS userId, so.mara_id AS maraId, so.adviser_id AS adviserId, so.adviser_id_2 AS adviserId2, so.official_id AS officialId, so.remarks AS remarks, so.closed_reason AS closedReason FROM b_service_order so, tb_adviser a WHERE so.is_delete = 0 AND a.id = so.adviser_id
AND so.type = #{type}
AND so.state != #{excludeState}
AND so.state IN
#{stateList}
AND so.review_state IN
#{reviewStateList}
AND so.mara_approval_date > #{startMaraApprovalDate} AND so.mara_approval_date < #{endMaraApprovalDate}
AND so.official_approval_date > #{startOfficialApprovalDate} AND so.official_approval_date < #{endOfficialApprovalDate}
AND a.region_id = #{regionId}
AND so.user_id = #{userId}
AND so.mara_id = #{maraId}
AND (so.adviser_id = #{adviserId} OR so.adviser_id_2 = #{adviserId})
AND so.official_id = #{officialId}
AND so.parent_id = #{parentId}
AND (SELECT COUNT(id) FROM b_service_order_review WHERE service_order_id = so.id AND official_state IS NOT NULL) <= 0
ORDER BY so.id DESCa LIMIT #{offset}, #{rows};

SELECT id, type, service_package_id AS servicePackageId, state FROM b_service_order WHERE is_delete = 0
AND parent_id = #{parentId}
ORDER BY gmt_create DESC;

mapper.update:
UPDATE b_service_order
SET gmt_modify = now(), finish_date = now()
WHERE id = #{id, jdbcType=INTEGER};
