mapper.update:
UPDATE b_service_order 
SET gmt_modify = now()

    , type = #{type}


    , service_id = #{serviceId}


    , school_id = #{schoolId}


    , is_settle = #{isSettle}


    , is_deposit_user = #{isDepositUser}


    , subagency_id = #{subagencyId}


    , is_pay = #{isPay}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , receivable = #{receivable}


    , discount = #{discount}


    , received = #{received}


    , payment_times = #{paymentTimes}


    , amount = #{amount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , user_id = #{userId}


    , mara_id = #{maraId}


    , adviser_id = #{adviserId}


    , official_id = #{officialId}


    , remarks = #{remarks}

WHERE id = #{id};

UPDATE b_service_order 
SET gmt_modify = now(), review_state = #{reviewState}
WHERE id = #{id};

mapper.select:
        SELECT count(*)
        FROM b_service_order
        WHERE is_delete = 0

			AND type = #{type}


			AND state = #{state}


			AND review_state = #{reviewState}


			AND user_id = #{userId}


			AND mara_id = #{maraId}


			AND adviser_id = #{adviserId}


			AND official_id = #{officialId}

        ;

SELECT id, gmt_create AS GmtCreate, finish_date AS finishDate, type, service_id AS serviceId, school_id AS schoolId, state, is_settle AS isSettle, is_deposit_user AS isDepositUser, subagency_id AS subagencyId, is_pay AS isPay, receive_type_id AS receiveTypeId, receive_date AS receiveDate, receivable, discount, received, payment_times AS paymentTimes, amount, gst, deduct_gst AS deductGst, bonus, user_id AS userId, mara_id AS maraId, adviser_id AS adviserId, official_id AS officialId, remarks FROM b_service_order WHERE is_delete = 0
AND type = #{type}
AND state = #{state}
AND review_state = #{reviewState}
AND user_id = #{userId}
AND mara_id = #{maraId}
AND adviser_id = #{adviserId}
AND official_id = #{officialId}
ORDER BY gmt_create DESC LIMIT #{offset}, #{rows};

mapper.update:
UPDATE b_service_order
SET gmt_modify = now(), is_delete = 1
WHERE id = #{id, jdbcType=INTEGER};

UPDATE b_service_order
SET gmt_modify = now(), finish_date = now()
WHERE id = #{id, jdbcType=INTEGER};
