mapper.select:
        SELECT
            id,
            gmt_create,
            gmt_modify,
            service_order_id,
            state,
            type,
            admin_user_id
        FROM b_service_order_review
        WHERE service_order_id = #{serviceOrderId}

			AND state = #{state}


			AND type = #{type}


			AND admin_user_id = #{adminUserId}

        ORDER BY gmt_create DESC;
