mapper.update:
UPDATE b_service_package_price 
SET gmt_modify = now()

    , min_price = #{minPrice}


    , max_price = #{maxPrice}


    , service_id = #{serviceId}


    , region_id = #{regionId}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_service_package_price WHERE service_id = #{serviceId, jdbcType=INTEGER}

    AND region_id = #{regionId}

;

SELECT spp.id AS id, spp.min_price AS minPrice, spp.max_price AS maxPrice, spp.service_package_id AS servicePackageId, spp.service_id AS serviceId, spp.region_id AS regionId, r.name AS regionName FROM b_service_package_price spp left join tb_region r on spp.region_id=r.id WHERE spp.service_id = #{serviceId, jdbcType=INTEGER}
AND spp.region_id = #{regionId}
ORDER BY spp.gmt_create ASC LIMIT #{offset}, #{rows};
