mapper.update:
UPDATE b_service_package_price 
SET gmt_modify = now()

    , min_price = #{minPrice}


    , max_price = #{maxPrice}


    , region_id = #{regionId}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_service_package_price WHERE service_package_id = #{servicePackageId, jdbcType=INTEGER}

    AND region_id = #{regionId}

;

SELECT
    id,
    min_price AS minPrice,
    max_price AS maxPrice,
    service_package_id AS servicePackageId,
    region_id AS regionId
FROM b_service_package_price WHERE service_package_id = #{servicePackageId, jdbcType=INTEGER}

    AND region_id = #{regionId}

ORDER BY gmt_create ASC
LIMIT #{offset}, #{rows};
