mapper.update:
UPDATE b_source 
SET gmt_modify = now()

    , name = #{name}


    , source_region_id = #{sourceRegionId}

WHERE id = #{id};

mapper.select:
SELECT id, gmt_create AS gmtCreate, name, source_region_id FROM b_source WHERE 1 = 1
AND source_region_id = #{sourceRegionId}
ORDER BY gmt_create DESC;
