mapper.update:
UPDATE b_visa 
SET gmt_modify = now()

    , handling_date = #{handlingDate}


    , user_id = #{userId}


    , state = #{state}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , service_id = #{serviceId}


    , service_order_id = #{serviceOrderId}


    , receivable = #{receivable}


    , received = #{received}


    , amount = #{amount}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , adviser_id = #{adviserId}


    , mara_id = #{maraId}


    , official_id = #{officialId}


    , remarks = #{remarks}


    , is_close = #{isClose}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_visa
WHERE 1 = 1

    AND name LIKE '%${keyword}%'


     AND handling_date > #{startHandlingDate} AND handling_date < #{endHandlingDate} 


     AND gmt_create > #{startDate} AND gmt_create < #{endDate} 


    AND adviser_id = #{adviserId}


    AND user_id = #{userId}

;

SELECT bv.id AS id, bv.gmt_create AS gmtCreate, bv.handling_date AS handlingDate, bv.user_id AS userId, bv.state AS state, bv.receive_type_id AS receiveTypeId, bv.receive_date AS receiveDate, bv.service_id AS serviceId, bv.service_order_id AS serviceOrderId, bv.receivable AS receivable, bv.received AS received, bv.amount AS amount, bv.discount AS discount, bv.gst AS gst, bv.deduct_gst AS deductGst, bv.bonus AS bonus, bv.adviser_id AS adviserId, bv.mara_id AS maraId, bv.official_id AS officialId, bv.remarks AS remarks, bv.is_close AS isClose, tbu.name AS userName, tbu.birthday AS birthday, tbu.phone AS phone FROM b_visa bv, tb_user tbu WHERE bv.user_id = tbu.id
AND bv.name LIKE '%${keyword}%'
AND bv.handling_date > #{startHandlingDate} AND bv.handling_date < #{endHandlingDate}
AND bv.gmt_create > #{startDate} AND bv.gmt_create < #{endDate}
AND bv.adviser_id = #{adviserId}
AND bv.user_id = #{userId}
ORDER BY bv.gmt_create DESC LIMIT #{offset}, #{rows};
