mapper.update:
UPDATE b_visa 
SET gmt_modify = now()

    , handling_date = #{handlingDate}


    , user_id = #{userId}


    , state = #{state}


    , commission_state = #{commissionState}


    , kj_approval_date = #{kjApprovalDate}


    , receive_type_id = #{receiveTypeId}


    , receive_date = #{receiveDate}


    , service_id = #{serviceId}


    , service_order_id = #{serviceOrderId}


    , payment_voucher_image_url_1 = #{paymentVoucherImageUrl1}


    , payment_voucher_image_url_2 = #{paymentVoucherImageUrl2}


    , payment_voucher_image_url_3 = #{paymentVoucherImageUrl3}


    , payment_voucher_image_url_4 = #{paymentVoucherImageUrl4}


    , payment_voucher_image_url_5 = #{paymentVoucherImageUrl5}


    , visa_voucher_image_url = #{visaVoucherImageUrl}


    , receivable = #{receivable}


    , received = #{received}


    , per_amount = #{perAmount}


    , amount = #{amount}


    , expect_amount = #{expectAmount}


    , sure_expect_amount = #{sureExpectAmount}


    , currency = #{currency}


    , exchange_rate = #{exchangeRate}


    , invoice_number = #{invoiceNumber}


    , gst = #{gst}


    , deduct_gst = #{deductGst}


    , bonus = #{bonus}


    , bonus_date = #{bonusDate}


    , adviser_id = #{adviserId}


    , mara_id = #{maraId}


    , official_id = #{officialId}


    , bank_check = #{bankCheck}


    , is_checked = #{isChecked}


    , remarks = #{remarks}


    , refuse_reason = #{refuseReason}


    , verify_code = #{verifyCode}


    , bank_date = #{bankDate}


    , is_close = #{isClose}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_visa bv, tb_adviser a, b_applicant ap, b_service_order so
WHERE a.id = bv.adviser_id AND bv.service_order_id = so.id AND ap.id = so.applicant_id

    AND bv.id = #{id}


    AND bv.name LIKE '%${keyword}%'


     AND bv.handling_date > #{startHandlingDate} AND bv.handling_date < #{endHandlingDate} 


    AND bv.state IN

        #{stateList}



    AND bv.commission_state IN

        #{commissionStateList}



     AND (bv.state="PENDING" OR bv.state="WAIT") 


     AND (bv.state="COMPLETE" OR bv.state="REVIEW") 



     AND bv.kj_approval_date > #{startKjApprovalDate} AND bv.kj_approval_date < #{endKjApprovalDate} 


     AND bv.gmt_create > #{startDate} AND bv.gmt_create < #{endDate} 


     AND bv.invoice_create > #{startInvoiceCreate} AND bv.invoice_create < #{endInvoiceCreate} 


    AND a.region_id IN

        #{regionIdList}



    AND bv.adviser_id = #{adviserId}


    AND bv.user_id = #{userId}


    AND (ap.surname LIKE '%${applicantName}%' OR ap.firstname LIKE '%${applicantName}%' OR CONCAT(ap.firstname, ' ', ap.surname) = #{applicantName})

;

SELECT count(*)
FROM b_visa
WHERE service_order_id = #{serviceOrderId}

    AND code != #{code}

;

SELECT bv.id AS id, bv.gmt_create AS gmtCreate, bv.code AS code, bv.handling_date AS handlingDate, bv.user_id AS userId, bv.state AS state, bv.commission_state AS commissionState, bv.kj_approval_date AS kjApprovalDate, bv.receive_type_id AS receiveTypeId, bv.receive_date AS receiveDate, bv.service_id AS serviceId, bv.service_order_id AS serviceOrderId, bv.installment_num AS installmentNum, bv.installment, bv.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, bv.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, bv.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, bv.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, bv.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, bv.visa_voucher_image_url AS visaVoucherImageUrl, bv.receivable AS receivable, bv.received AS received, bv.per_amount AS perAmount, bv.amount AS amount, bv.expect_amount AS expectAmount, bv.sure_expect_amount AS sureExpectAmount, bv.currency, bv.exchange_rate AS exchangeRate, bv.discount AS discount, bv.invoice_number AS invoiceNumber, bv.gst AS gst, bv.deduct_gst AS deductGst, bv.bonus AS bonus, bv.bonus_date AS bonusDate, bv.adviser_id AS adviserId, bv.mara_id AS maraId, bv.official_id AS officialId, bv.bank_check AS bankCheck, bv.is_checked AS isChecked, bv.remarks AS remarks, bv.refuse_reason AS refuseReason, bv.verify_code AS verifyCode, bv.bank_date AS bankDate, bv.is_close AS isClose, bv.invoice_create AS invoiceCreate, tbu.name AS userName, tbu.birthday AS birthday, tbu.phone AS phone, so.applicant_id AS applicantId FROM b_visa bv, tb_user tbu, tb_adviser a, b_service_order so LEFT JOIN b_applicant ap ON ap.id = so.applicant_id WHERE bv.user_id = tbu.id AND a.id = bv.adviser_id AND bv.service_order_id = so.id
AND bv.id = #{id}
AND bv.name LIKE '%${keyword}%'
AND bv.handling_date > #{startHandlingDate} AND bv.handling_date < #{endHandlingDate}
AND bv.state IN
#{stateList}
AND (bv.state="PENDING" OR bv.state="WAIT")
AND (bv.state="COMPLETE" OR bv.state="REVIEW")
AND bv.commission_state IN
#{commissionStateList}
AND bv.kj_approval_date > #{startKjApprovalDate} AND bv.kj_approval_date < #{endKjApprovalDate}
AND bv.gmt_create > #{startDate} AND bv.gmt_create < #{endDate}
AND bv.invoice_create > #{startInvoiceCreate} AND bv.invoice_create < #{endInvoiceCreate}
AND a.region_id IN
#{regionIdList}
AND bv.adviser_id = #{adviserId}
AND bv.user_id = #{userId}
AND tbu.name LIKE '%${userName}%'
AND (ap.surname LIKE '%${applicantName}%' OR ap.firstname LIKE '%${applicantName}%' OR CONCAT(ap.firstname, ' ', ap.surname) = #{applicantName})
${orderBy} LIMIT #{offset}, #{rows};

SELECT visa_expiration_date,`name`,id from tb_user where
adviser_id = #{adviserId} and
DATE_FORMAT(visa_expiration_date,'%Y') != DATE_FORMAT('1970-01-01','%Y') ORDER BY visa_expiration_date ASC limit #{pageNum},#{pageSize} ;
