mapper.update:
UPDATE b_visa_official
SET gmt_modify = now(),
kj_approval_date = now()

    , handling_date = #{handling_date}


    , commissionAmount = #{commissionAmount}


    , state = #{state}

where id = #{id}

UPDATE b_visa_official
SET gmt_modify = now()

    , handling_date = #{handlingDate}

where id = #{id}

mapper.select:
SELECT count(*)
FROM b_visa_official
WHERE service_order_id = #{serviceOrderId}

    AND code != #{code}

;

SELECT bv.id AS id, bv.gmt_create AS gmtCreate, bv.code AS code, bv.handling_date AS handlingDate, bv.user_id AS userId, bv.state AS state, bv.commission_state AS commissionState, bv.kj_approval_date AS kjApprovalDate, bv.receive_type_id AS receiveTypeId, bv.receive_date AS receiveDate, bv.service_id AS serviceId, bv.service_order_id AS serviceOrderId, bv.installment_num AS installmentNum, bv.installment, bv.payment_voucher_image_url_1 AS paymentVoucherImageUrl1, bv.payment_voucher_image_url_2 AS paymentVoucherImageUrl2, bv.payment_voucher_image_url_3 AS paymentVoucherImageUrl3, bv.payment_voucher_image_url_4 AS paymentVoucherImageUrl4, bv.payment_voucher_image_url_5 AS paymentVoucherImageUrl5, bv.visa_voucher_image_url AS visaVoucherImageUrl, bv.receivable AS receivable, bv.received AS received, bv.per_amount AS perAmount, bv.amount AS amount, bv.expect_amount AS expectAmount, bv.sure_expect_amount AS sureExpectAmount, bv.currency, bv.exchange_rate AS exchangeRate, bv.discount AS discount, bv.invoice_number AS invoiceNumber, bv.gst AS gst, bv.deduct_gst AS deductGst, bv.bonus AS bonus, bv.bonus_date AS bonusDate, bv.adviser_id AS adviserId, bv.mara_id AS maraId, bv.official_id AS officialId, bv.bank_check AS bankCheck, bv.is_checked AS isChecked, bv.remarks AS remarks, bv.refuse_reason AS refuseReason, bv.verify_code AS verifyCode, bv.bank_date AS bankDate, bv.is_close AS isClose, bv.invoice_create AS invoiceCreate, tu.name AS userName, tu.birthday AS birthday, tu.phone AS phone, so.applicant_id AS applicantId, bv.commissionAmount, bv.predict_commission predictCommission, ifnull(br.amount,0.0) as refund, so.submitIbDate as submitIbDate, bv.calculation, bv.predict_commissionAmount as predictCommissionAmount from b_visa_official bv JOIN b_service_order so ON bv.service_order_id = so.id left JOIN tb_user tu ON bv.user_id = tu.id left join b_refund br on br.visa_id= bv.id left join b_official bo on bo.id = bv.official_id join b_applicant ba on ba.id = so.applicant_id
WHERE 1=1
and bv.official_id = #{officialId}
AND bv.gmt_create > #{startDate} AND bv.gmt_create < #{endDate}
and bv.state = "REVIEW"
and bv.state = "COMPLETE"
and bv.id = #{id}
AND bo.region_id IN
#{regionIdList}
and tu.name LIKE '%${userName}%'
AND (ba.surname LIKE '%${applicantName}%' OR ba.firstname LIKE '%${applicantName}%' OR CONCAT(ba.firstname, ' ', ba.surname) LIKE '%${applicantName}%')
AND bv.handling_date > #{startHandlingDate} AND bv.handling_date < #{endHandlingDate}
${orderBy}
limit #{offset} ,#{pageSize}

SELECT
count(*)
FROM
b_visa_official co
JOIN b_service_order so ON co.service_order_id = so.id
LEFT JOIN tb_user tu ON co.user_id = tu.id
LEFT JOIN b_applicant ba ON so.applicant_id = ba.id
LEFT JOIN b_official bo ON bo.id = co.official_id
WHERE 1=1

    and co.official_id = #{officialId}



     AND co.gmt_create > #{startDate} AND co.gmt_create < #{endDate} 



    and co.id = #{id}


    and co.state="REVIEW"


    and co.state="COMPLETE"



    and tu.name = #{userName}


    AND (ba.surname LIKE '%${applicantName}%' OR ba.firstname LIKE '%${applicantName}%' OR CONCAT(ba.firstname, ' ', ba.surname) LIKE '%${applicantName}%')



     AND co.handling_date > #{startHandlingDate} AND co.handling_date < #{endHandlingDate} 


    AND bo.region_id IN

        #{regionIdList}

mapper.update:
