mapper.update:
UPDATE tb_consultation 
SET gmt_modify = now()

    , remind_date = #{remindDate}


    , remind_contents = #{remindContents}

WHERE id = #{id};

mapper.select:
SELECT c.id, c.gmt_create AS gmtCreate, c.gmt_modify AS gmtModify, c.user_id AS userId, c.contents, c.remind_date AS remindDate, c.remind_contents AS remindContents FROM tb_consultation c, tb_user u WHERE c.user_id = u.id AND c.remind_date < #{date}
AND u.adviser_id = #{adviserId}
ORDER BY c.gmt_create DESC;
