mapper.update:
UPDATE tb_subject 
SET gmt_modify = now(), state = #{state}
WHERE id = #{id};

UPDATE tb_subject 
SET gmt_modify = now()

    , name = #{name}


    , logo = #{logo}


    , start_date = #{startDateTimpstamp, jdbcType=TIMESTAMP}


    , end_date = #{endDateTimpstamp, jdbcType=TIMESTAMP}


    , category_id = #{categoryId}


    , pre_amount = #{preAmount}


    , codex = #{codex}


    , details = #{details}


    , region_ids = #{regionIds}


    , weight = #{weight}

WHERE id = #{id} AND state IN ('WAIT', 'START');

UPDATE tb_subject 
SET gmt_modify = now(), weight = #{weight}
WHERE id = #{id};

UPDATE tb_subject 
SET gmt_modify = now(), weight = (weight + 1)
WHERE weight > #{weight};

UPDATE tb_subject 
SET gmt_modify = now(), category_id = #{categoryId}
WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM tb_subject
WHERE state != 'DELETE'

    AND name LIKE '%${keyword}%'


    AND state = #{state}


    AND category_id = #{categoryId}

;

SELECT id, name, type, parent_id AS parentId, logo, start_date AS startDate, end_date AS endDate, state, category_id AS categoryId, weight FROM tb_subject WHERE state != 'DELETE'
AND name LIKE '%${keyword}%'
AND state = #{state}
AND category_id = #{categoryId}
ORDER BY gmt_create DESC LIMIT #{offset}, #{rows};
