mapper.select:
SELECT count(*)
FROM tb_user
WHERE 1 = 1

	AND name = #{name}


	AND auth_nickname = #{authNickname}


	AND phone = #{phone}

;

SELECT id, gmt_create AS gmtCreate, name, email, auth_type AS authType, auth_nickname AS authNickname, auth_username AS authUsername, phone, balance, adviser_id AS adviserId FROM tb_user WHERE 1 = 1
AND name = #{name}
AND auth_nickname = #{authNickname}
AND phone = #{phone}
ORDER BY gmt_create DESC LIMIT #{offset}, #{rows};

mapper.update:
UPDATE tb_user
SET gmt_modify = now(), adviser_id = #{adviserId}
WHERE id = #{id};

UPDATE tb_user
SET gmt_modify = now(), balance = #{balance}
WHERE id = #{id};
