mapper.select:
SELECT count(*)
FROM tb_user
WHERE auth_type != 'V'

    AND name LIKE '%${name}%'


    AND auth_type = #{authType}


    AND auth_nickname = #{authNickname}


    AND phone = #{phone}


    AND wechat_username = #{wechatUsername}


    AND adviser_id = #{adviserId}


    AND region_id IN

        #{regionIdList}



    AND ut.tag_id = #{tagId}

;

select count(*) from tb_user where auth_type != 'V' AND DATE_FORMAT(gmt_create, '%Y%m') = DATE_FORMAT(CURDATE(), '%Y%m')

    AND adviser_id = #{adviserId}

;

SELECT u.id AS id, u.gmt_create AS gmtCreate, u.name AS name, u.birthday AS birthday, u.email AS email, u.wechat_username AS wechatUsername, u.first_controller_contents AS firstControllerContents, u.visa_code AS visaCode, u.visa_expiration_date AS visaExpirationDate, u.source AS source, u.auth_type AS authType, u.auth_nickname AS authNickname, u.auth_username AS authUsername, u.area_code AS areaCode, u.phone AS phone, u.balance AS balance, u.adviser_id AS adviserId, u.region_id AS regionId FROM tb_user u LEFT JOIN b_user_tag ut ON u.id = ut.user_id WHERE u.auth_type != 'V'
AND u.name LIKE '%${name}%'
AND u.auth_type = #{authType}
AND u.auth_nickname = #{authNickname}
AND u.phone = #{phone}
AND u.wechat_username = #{wechatUsername}
AND u.id in (SELECT user_id FROM tb_user_adviser WHERE adviser_id = #{adviserId})
AND (u.region_id IN
#{regionIdList}
OR u.region_id = 0)
AND ut.tag_id = #{tagId}
GROUP BY u.id
ORDER BY u.${orderByField}
DESC
LIMIT #{offset}, #{rows};

mapper.update:
UPDATE tb_user
SET gmt_modify = now()

    , name = #{name, jdbcType=VARCHAR}


    , auth_nickname = #{authNickname, jdbcType=VARCHAR}


    , birthday = #{birthday}


    , phone = #{phone}


    , area_code = #{areaCode}


    , wechat_username = #{wechatUsername}


    , first_controller_contents = #{firstControllerContents}


    , visa_code = #{visaCode}


    , visa_expiration_date = #{visaExpirationDate}


    , source = #{source}

WHERE id = #{id};

UPDATE tb_user
SET gmt_modify = now(), adviser_id = #{adviserId}
WHERE id = #{id};

UPDATE tb_user
SET gmt_modify = now(), balance = #{balance}
WHERE id = #{id};

UPDATE tb_user
SET gmt_modify = now() , birthday = #{dob}
WHERE id = #{id};

UPDATE tb_user
SET auth_openid = #{authOpenid} ,auth_type = 'WECHAT_WORK'
WHERE phone = #{phone};

UPDATE tb_user
SET gmt_modify = now()

    , auth_nickname = #{authNickname, jdbcType=VARCHAR}


    , birthday = #{birthday}


    , phone = #{phone}


    , area_code = #{areaCode}


    , wechat_username = #{wechatUsername}


    , first_controller_contents = #{firstControllerContents}


    , visa_code = #{visaCode}


    , visa_expiration_date = #{visaExpirationDate}


    , source = #{source}

WHERE auth_type = 'WECHAT_WORK' and auth_openid = #{authOpenid};
