mapper.select:
select count(id) from `b_finance_code`


         AND bank_date > #{bankDateStart} AND bank_date < #{bankDateEnd} 


        AND region_id = #{regionId}

select id , gmt_create AS gmtCreate, gmt_modify as gmtModify, bank_date as bankDate, user_id as userId, is_income as isIncome, money , balance , adviser_id as adviserId, business , amount , order_id as orderId, comment from `b_finance_code`
AND bank_date > #{bankDateStart} AND bank_date < #{bankDateEnd}
AND region_id = #{regionId}
order by bank_date DESC limit #{pageNumber},#{pageSize} ;

mapper.update:
update  `b_finance_code`
set gmt_modify = now()

    ,  user_id = #{userId}


    , adviser_id = #{adviserId}


    , business = #{business}


    , amount = #{amount}


    , order_id = #{orderId}


    , code = #{code}

where id = #{id};

update `b_finance_bank` set gmt_modify = now()

    , bank = #{bank}


    , bsb = #{bsb}


    , accountno = #{accountNo}


    , simple = #{simple}


    , is_delete = #{delete}

where id = #{id};
