mapper.select:
select id,user_name,start_time,spend_time,base_path,uri,url,method,ip,parameter,result,description
from b_web_log
where 1 = 1

    AND service_order_id = #{serviceOrderId}

${orderBy}
LIMIT #{offset}, #{rows};

select count(*) from b_web_log
where 1 = 1

    AND service_order_id = #{serviceOrderId}
