mapper.select:
select id,user_id,role,start_time,spend_time,base_path,uri,url,method,ip,parameter,result,description,service_order_id
from b_web_log
where 1 = 1

    AND service_order_id = #{serviceOrderId}

LIMIT #{offset}, #{rows};

select count(*) from b_web_log
where 1 = 1

    AND service_order_id = #{serviceOrderId}
