mapper.select:
SELECT id, gmt_create AS gmtCreate, gmt_modify AS gmtModify, school_institution_id AS schoolInstitutionId, username AS userName, content AS content, parent_id AS parentId, to_username AS toUserName FROM b_school_institution_comment
AND school_institution_id = #{schoolInstitutionId}
AND parent_id = #{parentId}
;
