mapper.update:
UPDATE b_service_package 
SET gmt_modify = now()

    , type = #{type}


    , service_id = #{serviceId}


    , num = #{num}

WHERE id = #{id};

mapper.select:
SELECT count(*)
FROM b_service_package sp, b_service s
WHERE sp.service_id = s.id AND sp.is_delete = 0 AND s.is_delete = 0

    AND sp.service_id = #{serviceId}

;

SELECT sp.id, sp.gmt_create AS GmtCreate, sp.type AS type, sp.service_id AS serviceId, s.name AS serviceName, sp.num FROM b_service_package sp, b_service s WHERE sp.service_id = s.id AND sp.is_delete = 0 AND s.is_delete = 0
AND sp.service_id = #{serviceId}
ORDER BY sp.num ASC LIMIT #{offset}, #{rows};

mapper.update:
UPDATE b_service_package 
SET gmt_modify = now(), is_delete = 1
WHERE id = #{id, jdbcType=INTEGER};
