mapper.update:
UPDATE tb_consultation 
SET gmt_modify = now()

    , state = #{state}


    , remind_date = #{remindDate}


    , remind_contents = #{remindContents}

WHERE id = #{id};

mapper.select:
SELECT id, gmt_create AS gmtCreate, gmt_modify AS gmtModify, user_id AS userId, contents, state, remind_date AS remindDate, remind_contents AS remindContents FROM tb_consultation WHERE user_id = #{userId, jdbcType=INTEGER}
AND state = #{state}
ORDER BY gmt_create DESC;

SELECT c.id, c.gmt_create AS gmtCreate, c.gmt_modify AS gmtModify, c.user_id AS userId, c.contents, c.state, c.remind_date AS remindDate, c.remind_contents AS remindContents FROM tb_consultation c, tb_user u WHERE state = 'ENABLED' AND c.user_id = u.id AND c.remind_date < #{date}
AND u.adviser_id = #{adviserId}
ORDER BY c.gmt_create DESC;
