mapper.update:
UPDATE b_subagency 
SET gmt_modify = now()

    , name = #{name}


    , commission_rate = #{commissionRate}

WHERE id = #{id};

mapper.select:
SELECT id, gmt_create AS GmtCreate, name, commission_rate AS CommissionRate FROM b_subagency WHERE is_delete = 0
AND name LIKE '%${keyword}%'
ORDER BY gmt_create DESC;

mapper.update:
UPDATE b_subagency 
SET gmt_modify = now(), is_delete = 1
WHERE id = #{id, jdbcType=INTEGER};
